var crcTable = crc64.MakeTable(crc64.ECMA)

func (cluster *Cluster) initMdbsproxy(oldmaster *ServerMonitor, proxy *Proxy) {
	// cluster.LogPrintf("INFO", "Init MdbShardProxy %s %s", proxy.Host, proxy.Port)

	params := fmt.Sprintf("?timeout=%ds", cluster.conf.Timeout)

	dsn := proxy.User + ":" + proxy.Pass + "@"
	dsn += "tcp(" + proxy.Host + ":" + proxy.Port + ")/" + params
	c, err := sqlx.Open("mysql", dsn)
	if err != nil {
		cluster.LogPrintf("ERROR", "Could not connect to MariaDB Sharding Proxy %s", err)
		cluster.mdbsBootstrap(proxy)
	}
	err = c.Ping()
	if err != nil {
		cluster.LogPrintf("ERROR", "Could not connect to MariaDB Sharding Proxy %s", err)
		cluster.mdbsBootstrap(proxy)
	}
	schemas, err := dbhelper.GetSchemas(cluster.master.Conn)
	if err != nil {
		cluster.LogPrintf("ERROR", "Could not fetch master schemas %s", err)
	}

	for _, s := range schemas {
		checksum64 := crc64.Checksum([]byte(s+"_"+cluster.GetName()), crcTable)

		query := "CREATE OR REPLACE SERVER s" + strconv.FormatUint(checksum64, 10) + " FOREIGN DATA WRAPPER mysql OPTIONS (HOST '" + cluster.master.Host + "', DATABASE '" + s + "', USER '" + cluster.master.User + "', PASSWORD '" + cluster.master.Pass + "', PORT " + cluster.master.Port + ")"
		_, err = c.Exec(query)
		if err != nil {
			cluster.LogPrintf("ERROR: query %s %s", query, err)
		}
		query = "CREATE DATABASE IF NOT EXISTS " + s
		_, err = c.Exec(query)
		if err != nil {
			cluster.LogPrintf("ERROR", "Failed query %s %s", query, err)
		}

	}
	tables, err := dbhelper.GetTables(cluster.master.Conn)
	if err != nil {
		cluster.LogPrintf("ERROR", "Could not fetch master tables %s", err)
	}
	for _, t := range tables {
		checksum64 := crc64.Checksum([]byte(t.Table_schema+"_"+cluster.GetName()), crcTable)

		query := "CREATE OR REPLACE TABLE " + t.Table_schema + "." + t.Table_name + " ENGINE=spider comment='wrapper \"mysql\", table \"" + t.Table_name + "\", srv \"s" + strconv.FormatUint(checksum64, 10) + "\"'"
		_, err = c.Exec(query)
		if err != nil {
			cluster.LogPrintf("ERROR: query %s %s", query, err)
		}
	}
	c.Close()
}

func (cluster *Cluster) refreshMdbsproxy(oldmaster *ServerMonitor, proxy *Proxy) {
	params := fmt.Sprintf("?timeout=%ds", cluster.conf.Timeout)

	dsn := proxy.User + ":" + proxy.Pass + "@"
	dsn += "tcp(" + proxy.Host + ":" + proxy.Port + ")/" + params
	c, err := sqlx.Open("mysql", dsn)
	if err != nil {
		cluster.LogPrintf("ERROR", "Could not connect to MariaDB Sharding Proxy %s", err)
		cluster.mdbsBootstrap(proxy)
	}
	err = c.Ping()
	if err != nil {
		cluster.LogPrintf("ERROR", "Could not connect to MariaDB Sharding Proxy %s", err)
		cluster.mdbsBootstrap(proxy)
	}
	schemas, err := dbhelper.GetSchemas(cluster.master.Conn)
	if err != nil {
		cluster.LogPrintf("ERROR", "Could not fetch master schemas %s", err)
	}

	for _, s := range schemas {
		checksum64 := crc64.Checksum([]byte(s+"_"+cluster.GetName()), crcTable)

		query := "CREATE OR REPLACE SERVER s" + strconv.FormatUint(checksum64, 10) + " FOREIGN DATA WRAPPER mysql OPTIONS (HOST '" + cluster.master.Host + "', DATABASE '" + s + "', USER '" + cluster.master.User + "', PASSWORD '" + cluster.master.Pass + "', PORT " + cluster.master.Port + ")"
		_, err = c.Exec(query)
		if err != nil {
			cluster.LogPrintf("ERROR: query %s %s", query, err)
		}
		query = "CREATE DATABASE IF NOT EXISTS " + s
		_, err = c.Exec(query)
		if err != nil {
			cluster.LogPrintf("ERROR", "Failed query %s %s", query, err)
		}

	}
	tables, err := dbhelper.GetTables(cluster.master.Conn)
	if err != nil {
		cluster.LogPrintf("ERROR", "Could not fetch master tables %s", err)
	}
	for _, t := range tables {
		checksum64 := crc64.Checksum([]byte(t.Table_schema+"_"+cluster.GetName()), crcTable)

		query := "CREATE TABLE IF NOT EXISTS " + t.Table_schema + "." + t.Table_name + " ENGINE=spider comment='wrapper \"mysql\", table \"" + t.Table_name + "\", srv \"s" + strconv.FormatUint(checksum64, 10) + "\"'"
		_, err = c.Exec(query)
		if err != nil {
			cluster.LogPrintf("ERROR", "Failed query %s %s", query, err)
		}
	}
	c.Close()
}

